import { useRef, useState } from "react";
import "./register.css"
import axios from "axios";


const Register = () =>{
    const name=useRef();
    const email=useRef();
    const studentNo=useRef();
    const Branch=useRef

    const handleClick = async (e) => {
        e.preventDefault();
        setError(null); // Clear previous errors
    
        if (passwordAgain.current.value !== password.current.value) {
          passwordAgain.current.setCustomValidity("Password does not match");
        } else {
          const user = {
            username: username.current.value,
            email: email.current.value,
            password: password.current.value,
          };
    
          try {
            await axios.post("/auth/register", user);
            history("/login");
          } catch (err) {
            console.error(err);
    
           if (err.response && err.response.status === 400 && err.response.data.error === 'Username already exists') {
              alert('Username already exists / Please choose a different username or/ Try to Log In');
            } else {
              alert('Registration failed. Please try again later.');
            }
          }
        }
      };
   
   
    return(
    <>
    <div className="register">
       <div className="container">
          <form className="loginBox" onSunmit={handliClick}>
           <h1>Register to Participate</h1>
           <label>Name:</label>
           <input type="text" required placeholder="Name" className="inputData"/>
           
           <label>Email:</label>
           <input type="email" required placeholder="Email" className="inputData"/>

           <label>Student No:</label>
           <input type="integer" required placeholder="Student No" className="inputData"/>

           <label>Branch:</label>
           <input type="text" required placeholder="Branch" className="inputData"/>
         </form>

         <button className="submitButton">Submit</button>
       </div>

    </div>
    
    </>
   )
}

export default Register
